import React from "react";
import { useParams } from "react-router-dom";
import ImageHelper from "./helper/ImageHelper";
import "./prodView.css";
const ProductViewer = (props) => {
  const obj = useParams();
  console.log(props);
  const { name, description, price, stock } = props.location.state;
  return (
    <div className="prod">
      <ImageHelper product={props.location.state} />
      <div className="prod__info text-white">
        <h5>brand:{name}</h5>
        <p>{description}</p>
        <h5>price:{price}</h5>
        <h5>stocks:{stock}</h5>
      </div>
      <div>
        <button className="btn btn-block btn-outline-success mt-2 mb-2">
          Add To Cart
        </button>
      </div>
    </div>
  );
};

export default ProductViewer;
